func TestVerifyCmd(t *testing.T) {

	server := test.MockTrustAuthorityServer(t)
	defer server.Close()

	configJson := `{"trustauthority_url":"` + server.URL + `"}`
	_ = os.WriteFile(confFilePath, []byte(configJson), 0600)
	defer os.Remove(confFilePath)

	tt := []struct {
		args        []string
		wantErr     bool
		description string
	}{
		{
			args: []string{
				constants.VerifyCmd,
				"--" + constants.ConfigOption,
				confFilePath,
				"--" + constants.TokenOption,
				token,
			},
			wantErr:     true,
			description: "Test with config file and a token",
		},
		{
			args: []string{
				constants.VerifyCmd,
				"--" + constants.TokenOption,
				token,
			},
			wantErr:     true,
			description: "Test without config file",
		},
		{
			args: []string{
				constants.VerifyCmd,
				"--" + constants.ConfigOption,
				"config-file.json",
				"--" + constants.TokenOption,
				token,
			},
			wantErr:     true,
			description: "Test with non-existent config file",
		},
		{
			args: []string{
				constants.VerifyCmd,
			},
			wantErr:     true,
			description: "Test without config file or token",
		},
		{
			args: []string{
				constants.VerifyCmd,
				"--" + constants.ConfigOption,
				confFilePath,
			},
			wantErr:     true,
			description: "Test without a token",
		},
	}

	for _, tc := range tt {
		_, err := execute(t, rootCmd, tc.args...)

		if tc.wantErr == true {
			assert.Error(t, err)
		} else {
			assert.NoError(t, err)
		}
	}
}

func TestVerifyCmd_MissingTrustAuthorityUrl(t *testing.T) {

	configJson := `{"trustauthority_url":""}`
	_ = os.WriteFile(confFilePath, []byte(configJson), 0600)
	defer os.Remove(confFilePath)
	_, err := execute(t, rootCmd, constants.VerifyCmd, "--"+constants.ConfigOption, confFilePath, "--"+constants.TokenOption, token)
	assert.Error(t, err)
}

func TestVerifyCmd_MalformedTrustAuthorityUrl(t *testing.T) {

	configJson := `{"trustauthority_url":":trustauthority.intel.com"}`
	_ = os.WriteFile(confFilePath, []byte(configJson), 0600)
	defer os.Remove(confFilePath)
	_, err := execute(t, rootCmd, constants.VerifyCmd, "--"+constants.ConfigOption, confFilePath, "--"+constants.TokenOption, token)
	assert.Error(t, err)
}
